ScriptGroup.script:
ar.mySkills = ar.mySkills or {}
ar.skills = ar.skills or {}
if not lfs.attributes(ar.file.."/skills") then
  lfs.mkdir(ar.file.."/skills")
end

Script.script:
ar.skills.iHave = function(_) local dontCare = { "Philosophy", "Galvanism", "Fitness", "Antidotes", "Frost", "Thermology", "Constitution", "Avoidance", }
ar.mySkills = {} if ars.myInfo.class then if io.exists(ar.file .. "/skills/skill list " .. ars.myInfo.class) then table.load(ar.file .. "/skills/skill list " .. ars.myInfo.class, ar.mySkills) return else sendGMCP("Char.Skills.Get " .. yajl.to_string({})) send(" ") end end
for i = 1, #gmcp.Char.Skills.Groups do if table.contains(dontCare, gmcp.Char.Skills.Groups[i].name) == false then ar.mySkills[gmcp.Char.Skills.Groups[i].name:lower()] = {} end end raiseEvent("ar.gotSkillGroups") end registerAnonymousEventHandler("ars.changeClass", "ar.skills.iHave")
